import React from "react";
import PropTypes from "react-proptypes";

import styled from "styled-components";
import Button from "@material-ui/core/Button";
import { Link } from "react-router-dom";

import { withRouter } from "react-router-dom";

const Container = styled.div`
  display: flex;
`;

const Row = styled.div`
  display: flex;
  flex: 1;
`;

const StyledLink = styled(Link)`
  && {
    display: inline-block;
    margin: auto;
  }
`;

const StyledButton = styled(Button)`
  && {
    color: white;
    background-color: gray;
    padding: 0.5rem;
    margin: auto;
    ${({ isActive }) => isActive && `background-color: lightgray`};
  }
`;

const Menu = props => {
  console.log(props);
  const path = props.location.pathname.replace("/", "");
  console.log(path);
  return (
    <Container>
      <Row>Logo</Row>
      <Row>
        <StyledLink to="about">
          <StyledButton isActive={path === "about"}>Om</StyledButton>
        </StyledLink>
        <StyledLink to="work">
          <StyledButton isActive={path === "work"}>Work</StyledButton>
        </StyledLink>
        <StyledLink to="skills">
          <StyledButton isActive={path === "skills"}>Skills</StyledButton>
        </StyledLink>
      </Row>
    </Container>
  );
};

export default withRouter(Menu);
