import React from "react";
import PropTypes from "react-proptypes";
import styled from "styled-components";
import SectionContainer from "../../shared/components/SectionContainer";
import StarFilledIcon from "@material-ui/icons/Star";
import StarEmptyIcon from "@material-ui/icons/StarBorder";
import StarHalfIcon from "@material-ui/icons/StarHalf";
import theme from "../../shared/theme";

// <Datag>
import { techniques } from "../../static/data";

const Content = styled.div`
  width: 50%;
  margin: auto;
`;
const Area = styled.div`
  padding: 2rem 0 0 0;
`;

const AreaTitle = styled.div`
  color: ${theme.colors.black.main};
  font-weight: bold;
  font-size: 1.2rem;
`;

const Technique = styled.div`
  display: flex;
`;

const TechniqueTitle = styled.div``;

const TechniqueRating = styled.div``;

const StyledStar = styled(StarIcon)`
  color: ${theme.colors.black.main};
`;
const Skills = () => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    if (i <= t.rating) {
      stars.push(<StarIcon />);
    } else if (!(i - t.rating) === i) {
      stars.push(<StarHalfIcon />);
    } else {
      stars.push(<StarEmptyIcon />);
    }
  }
  return (
    <SectionContainer>
      <Content>
        <Area>
          <AreaTitle>Front End</AreaTitle>
          {techniques.map((t, key) => {
            return (
              <Technique>
                <TechniqueTitle>{t.title}</TechniqueTitle>
                <TechniqueRating>{stars}</TechniqueRating>
              </Technique>
            );
          })}
        </Area>
      </Content>
    </SectionContainer>
  );
};

export default Skills;
